#import "HelpViewController.h"

@interface HelpViewController ()

@end

@implementation HelpViewController

@synthesize screenInd,helpTxt;

- (void) viewDidLoad {
    
    NSString *screen1 = @"<html><head><meta content=\"text/html; charset=UTF-8\" http-equiv=\"content-type\"><style type=\"text/css\">ol{margin:0;padding:0}.c0{orphans:2;widows:2;direction:ltr;height:11pt}.c6{background-color:#fff;max-width:451.4pt;padding:72pt 72pt 72pt 72pt}.c5{font-size: 12pt;color: #222222}.c2{orphans:2;widows:2;direction:ltr}.c7{font-size: 9.5pt;color: #a61c00}.c1{font-size: 9.5pt;color: #222222}.c3{text-decoration:underline}.c4{font-weight:bold}.title{padding-top:0pt;color:#000000;font-size:26pt;padding-bottom:3pt;font-family:\"Arial\";line-height:1.15;page-break-after:avoid;orphans:2;widows:2;text-align:left}.subtitle{padding-top:0pt;color:#666666;font-size:15pt;padding-bottom:16pt;font-family:\"Arial\";line-height:1.15;page-break-after:avoid;orphans:2;widows:2;text-align:left}li{color:#000000;font-size:11pt;font-family:\"Arial\"}p{margin:0;color:#000000;font-size:11pt;font-family:\"Arial\"}h1{padding-top:20pt;color:#000000;font-size:20pt;padding-bottom:6pt;font-family:\"Arial\";line-height:1.15;page-break-after:avoid;orphans:2;widows:2;text-align:left}h2{padding-top:18pt;color:#000000;font-size:16pt;padding-bottom:6pt;font-family:\"Arial\";line-height:1.15;page-break-after:avoid;orphans:2;widows:2;text-align:left}h3{padding-top:16pt;color:#434343;font-size:14pt;padding-bottom:4pt;font-family:\"Arial\";line-height:1.15;page-break-after:avoid;orphans:2;widows:2;text-align:left}h4{padding-top:14pt;color:#666666;font-size:12pt;padding-bottom:4pt;font-family:\"Arial\";line-height:1.15;page-break-after:avoid;orphans:2;widows:2;text-align:left}h5{padding-top:12pt;color:#666666;font-size:11pt;padding-bottom:4pt;font-family:\"Arial\";line-height:1.15;page-break-after:avoid;orphans:2;widows:2;text-align:left}h6{padding-top:12pt;color:#666666;font-size:11pt;padding-bottom:4pt;font-family:\"Arial\";line-height:1.15;page-break-after:avoid;font-style:italic;orphans:2;widows:2;text-align:left}</style></head><body class=\"c6\"><p class=\"c2\"><span class=\"c5 c3\">Top of Screen</span></p><p class=\"c0\"><span class=\"c1\"></span></p><p class=\"c2\"><span class=\"c1 c4\">Slide - Pan - Tilt</span><span class=\"c1\">&nbsp;axis buttons provide access to detailed settings for each motor port. </span></p><p class=\"c0\"><span class=\"c1\"></span></p><p class=\"c2\"><span class=\"c1 c4\">Slide = Motor Port 1</span><span class=\"c1\">, </span><span class=\"c1 c4\">Pan = Motor Port 2</span><span class=\"c1\">&nbsp;and </span><span class=\"c1 c4\">Tilt = Motor Port 3</span><span class=\"c1\">&nbsp;on the NMX. &nbsp;In 2P mode the set distance between the start and stop positions is displayed below each button (NOTE: this does require the correct motor gear ratio and rig ratio be set in the axis detail settings)</span></p><p class=\"c0\"><span class=\"c1\"></span></p><p class=\"c2\"><span class=\"c5 c3\">Middle Joystick Area</span></p><p class=\"c0\"><span class=\"c1 c3\"></span></p><p class=\"c2\"><span class=\"c1 c4\">Slider Control and Pan/TIlt Joystick</span><span class=\"c1\">&nbsp;are used to move the rig into keyframe positions.</span></p><p class=\"c0\"><span class=\"c1\"></span></p><p class=\"c2\"><span class=\"c1 c4\">2P vs 3P</span><span class=\"c1 c4\">&nbsp;Keyframe Toggle (top right) </span><span class=\"c1\">2P (two point) allows basic start and end of movement to be set vs 3P (three point) allows start, middle and end points to be set for more complex moves.</span></p><p class=\"c0\"><span class=\"c1\"></span></p><p class=\"c2\"><span class=\"c1 c4\">Lock Axis Toggle (lower left)</span><span class=\"c1\">&nbsp;isolates pan or tilt movements, when unlocked movement is not restricted, pan and tilt will be able move at the same time.</span></p><p class=\"c0\"><span class=\"c1\"></span></p><p class=\"c2\"><span class=\"c1 c4\">Gear Symbol (lower right)</span><span class=\"c1\">&nbsp;App settings; give NMX a unique name, check battery level and set voltage range, app version.</span></p><p class=\"c0\"><span class=\"c1 c4\"></span></p><p class=\"c2\"><span class=\"c5 c3\">Lower Screen</span></p><p class=\"c0\"><span class=\"c1 c4\"></span></p><p class=\"c2\"><span class=\"c1 c4\">Start and End buttons</span><span class=\"c1\">&nbsp;- tap to set current rig position as Start or End of movement, NOTE: End can be set before start, this can be helpful if you don&rsquo;t want to waste time/energy having the rig return to the start when you are ready to run a program.</span></p><p class=\"c0\"><span class=\"c1\"></span></p><p class=\"c2\"><span class=\"c1 c4\">Flip (in 2P mode) button </span><span class=\"c1\">Swaps start and end points </span><span class=\"c1 c4\">Mid (in 3P mode) button</span><span class=\"c1\">&nbsp;sets middle keyframe</span></p><p class=\"c0\"><span class=\"c1\"></span></p><p class=\"c2\"><span class=\"c1 c4\">Fire Camera </span><span class=\"c1\">Sends a single </span><span class=\"c1\">camera test shot</span></p></body></html>";
    
    NSString *screen2 = @"<html><head><meta content=\"text/html; charset=UTF-8\" http-equiv=\"content-type\"><style type=\"text/css\">ol{margin:0;padding:0}.c0{orphans:2;widows:2;direction:ltr;height:11pt}.c6{background-color:#fff;max-width:451.4pt;padding:72pt 72pt 72pt 72pt}.c5{font-size: 12pt;color: #222222}.c2{orphans:2;widows:2;direction:ltr}.c7{font-size: 9.5pt;color: #a61c00}.c1{font-size: 9.5pt;color: #222222}.c3{text-decoration:underline}.c4{font-weight:bold}.title{padding-top:0pt;color:#000000;font-size:26pt;padding-bottom:3pt;font-family:\"Arial\";line-height:1.15;page-break-after:avoid;orphans:2;widows:2;text-align:left}.subtitle{padding-top:0pt;color:#666666;font-size:15pt;padding-bottom:16pt;font-family:\"Arial\";line-height:1.15;page-break-after:avoid;orphans:2;widows:2;text-align:left}li{color:#000000;font-size:11pt;font-family:\"Arial\"}p{margin:0;color:#000000;font-size:11pt;font-family:\"Arial\"}h1{padding-top:20pt;color:#000000;font-size:20pt;padding-bottom:6pt;font-family:\"Arial\";line-height:1.15;page-break-after:avoid;orphans:2;widows:2;text-align:left}h2{padding-top:18pt;color:#000000;font-size:16pt;padding-bottom:6pt;font-family:\"Arial\";line-height:1.15;page-break-after:avoid;orphans:2;widows:2;text-align:left}h3{padding-top:16pt;color:#434343;font-size:14pt;padding-bottom:4pt;font-family:\"Arial\";line-height:1.15;page-break-after:avoid;orphans:2;widows:2;text-align:left}h4{padding-top:14pt;color:#666666;font-size:12pt;padding-bottom:4pt;font-family:\"Arial\";line-height:1.15;page-break-after:avoid;orphans:2;widows:2;text-align:left}h5{padding-top:12pt;color:#666666;font-size:11pt;padding-bottom:4pt;font-family:\"Arial\";line-height:1.15;page-break-after:avoid;orphans:2;widows:2;text-align:left}h6{padding-top:12pt;color:#666666;font-size:11pt;padding-bottom:4pt;font-family:\"Arial\";line-height:1.15;page-break-after:avoid;font-style:italic;orphans:2;widows:2;text-align:left}</style></head><body class=\"c6\"><p class=\"c2\"><span class=\"c5 c3\">Main Axis Movement Controls</span></p><br><p class=\"c2\"><span class=\"c1 c4\">One Touch Move </span><span class=\"c1\">one tap</span><span class=\"c1\">&nbsp;provides isolated continuous hands-free motor movement, the first tap starts the movement and a second tap stops the motion (NOTE: top speed is affected by response and sensitivity below) Best used when you have a longer distance to travel.</span></p><p class=\"c0\"><span class=\"c1\"></span></p><p class=\"c2\"><span class=\"c1 c4\">Response</span><span class=\"c1\">&nbsp;determines the &lsquo;speed range&rsquo; the motors will move when you interact with the controls; &lsquo;High&rsquo; is the fastest range whereas &lsquo;Low&rsquo; is the slowest range.</span></p><p class=\"c0\"><span class=\"c1\"></span></p><p class=\"c2\"><span class=\"c1 c4\">Sensitivity </span><span class=\"c1\">limits the speed range within a Response range, for example if you want extremely detailed and slow movement from the axis you would set the Response to &lsquo;Low&rsquo; and set the Sensitivity to 25%.</span></p><p class=\"c0\"><span class=\"c1\"></span></p><p class=\"c2\"><span class=\"c1 c4\">Pan/Tilt Sliders Toggle </span><span class=\"c1\">this option will switch between Pan/Tilt joystick on the main screen or two individual sliders, this further helps with isolating individual axis of movement.</span><span class=\"c1 c4\">&nbsp;</span></p><p class=\"c2\"><span class=\"c1 c4\">Invert Direction Toggle</span><span class=\"c1\">&nbsp;this option will reverse the direction of movement for the individual axis, this can be helpful if the control movement is reverse of what is desired.</span></p><p class=\"c0\"><span class=\"c1\"></span></p><p class=\"c0\"><span class=\"c1\"></span></p><p class=\"c2\"><span class=\"c5 c3\">Detail Axis Controls</span><br></p><p class=\"c2\"><span class=\"c1 c4\">Power Save</span><span class=\"c1\">&nbsp;When activated (&lsquo;on&rsquo;) the motor drivers will turn off or &lsquo;sleep&rsquo; when not in motion (thus saving power) By default the setting is &lsquo;off&rsquo; providing constant power to the motors which results in maximum position holding strength (indicated by steady blue axis LED). When executing low motor strain movements that do not require holding power against gravity (for example: a horizontal slide where gravity is not pulling on the motor while at rest) power save can be turned &quot;on&quot; allowing the motor to sleep between movements and less battery consumption (See manual for more info)</span></p><p class=\"c0\"><span class=\"c1\"></span></p><p class=\"c2\"><span class=\"c1 c4\">Backlash</span><span class=\"c1\">&nbsp;is used to take up any &lsquo;slack&rsquo; when the motors are reversing, a common case is when the motors return to a start position and then begin movement in the opposite direction. A typical range is 0-300, a perfect setting will only result in a very very slightly move forward before the program begins. If the axis &lsquo;jumps or lurches forward&rsquo; before starting a program then the backlash is set TOO HIGH.</span></p><p class=\"c0\"><span class=\"c1\"></span></p><p class=\"c2\"><span class=\"c1 c4\">Motor Gear Ratio</span><span class=\"c1\">&nbsp;informs the app what motor is connected to the axis port, this is only important if you want to set an exact distance or angle in 2 Point Mode.</span></p><p class=\"c0\"><span class=\"c1\"></span></p><p class=\"c2\"><span class=\"c1 c4\">Rig Ratio</span><span class=\"c1\">&nbsp;informs the app if the motor is running a Stage R rotary unit or Stage Zero/One linear slider, custom linear is also available this number represents the distance in inches per revolution or the &lsquo;pitch diameter&rsquo; of the pulley used to drive a slider.</span></p><p class=\"c0\"><span class=\"c1\"></span></p><p class=\"c2\"><span class=\"c1 c4\">Overall Distance</span><span class=\"c1\">&nbsp;allows you to set an exact distance of travel in 2 Point Mode (this option is locked out in 3 Point Mode), the Motor Gear Ratio and Rig Ratio must be set correctly for this setting to work accurately. Best way to use this feature is to first set the Start and then set the End partially in the direction you want the axis to travel. Then edit the overall distance to the desired distance or angle.</span></p></body></html>";
    
    
    NSString *screen3 = @"<html><head><meta content=\"text/html; charset=UTF-8\" http-equiv=\"content-type\"><style type=\"text/css\">ol{margin:0;padding:0}.c0{orphans:2;widows:2;direction:ltr;height:11pt}.c6{background-color:#fff;max-width:451.4pt;padding:72pt 72pt 72pt 72pt}.c5{font-size: 12pt;color: #222222}.c2{orphans:2;widows:2;direction:ltr}.c7{font-size: 9.5pt;color: #a61c00}.c1{font-size: 9.5pt;color: #222222}.c3{text-decoration:underline}.c4{font-weight:bold}.title{padding-top:0pt;color:#000000;font-size:26pt;padding-bottom:3pt;font-family:\"Arial\";line-height:1.15;page-break-after:avoid;orphans:2;widows:2;text-align:left}.subtitle{padding-top:0pt;color:#666666;font-size:15pt;padding-bottom:16pt;font-family:\"Arial\";line-height:1.15;page-break-after:avoid;orphans:2;widows:2;text-align:left}li{color:#000000;font-size:11pt;font-family:\"Arial\"}p{margin:0;color:#000000;font-size:11pt;font-family:\"Arial\"}h1{padding-top:20pt;color:#000000;font-size:20pt;padding-bottom:6pt;font-family:\"Arial\";line-height:1.15;page-break-after:avoid;orphans:2;widows:2;text-align:left}h2{padding-top:18pt;color:#000000;font-size:16pt;padding-bottom:6pt;font-family:\"Arial\";line-height:1.15;page-break-after:avoid;orphans:2;widows:2;text-align:left}h3{padding-top:16pt;color:#434343;font-size:14pt;padding-bottom:4pt;font-family:\"Arial\";line-height:1.15;page-break-after:avoid;orphans:2;widows:2;text-align:left}h4{padding-top:14pt;color:#666666;font-size:12pt;padding-bottom:4pt;font-family:\"Arial\";line-height:1.15;page-break-after:avoid;orphans:2;widows:2;text-align:left}h5{padding-top:12pt;color:#666666;font-size:11pt;padding-bottom:4pt;font-family:\"Arial\";line-height:1.15;page-break-after:avoid;orphans:2;widows:2;text-align:left}h6{padding-top:12pt;color:#666666;font-size:11pt;padding-bottom:4pt;font-family:\"Arial\";line-height:1.15;page-break-after:avoid;font-style:italic;orphans:2;widows:2;text-align:left}</style></head><body class=\"c6\"><p class=\"c2\"><span class=\"c5 c3\">Top of Screen</span></p><p class=\"c0\"><span class=\"c1\"></span></p><p class=\"c2\"><span class=\"c1\">Switch to choose between a </span><span class=\"c1 c4\">Timelapse</span><span class=\"c1\">&nbsp;or </span><span class=\"c1 c4\">Video</span><span class=\"c1\">&nbsp;move. In </span><span class=\"c1 c4\">Timelapse</span><span class=\"c1\">&nbsp;mode the camera trigger timing options are available as well as the choice of </span><span class=\"c1 c4\">SMS</span><span class=\"c1\">&nbsp;(shoot move shoot) style or </span><span class=\"c1 c4\">Continuous</span><span class=\"c1\">&nbsp;style. In </span><span class=\"c1 c4\">SMS</span><span class=\"c1\">&nbsp;the system will only move the motors forward between camera exposures, in Continuous the motors will slowly move from start to end without stopping.</span></p><p class=\"c0\"><span class=\"c1\"></span></p><p class=\"c2\"><span class=\"c5 c3\">Input area in Timelapse Mode</span></p><p class=\"c0\"><span class=\"c1\"></span></p><p class=\"c2\"><span class=\"c1 c4\">Exposure:</span><span class=\"c1\">&nbsp;is to inform the system what exposure you have set on the camera. Below the 0.3 second minimum exposure this setting is moot, the setting is only important with longer exposures in SMS mode to ensure the motor movement holds off until the exposure is finished </span></p><p class=\"c0\"><span class=\"c1\"></span></p><p class=\"c2\"><span class=\"c1 c4\">Buffer:</span><span class=\"c1\">&nbsp;is simply the Interval minus the Exposure, this value can only be set by adjusting the Exposure or overall Interval</span></p><p class=\"c0\"><span class=\"c1\"></span></p><p class=\"c2\"><span class=\"c1 c4\">Interval:</span><span class=\"c1\">&nbsp;is the overall time between shots, don&rsquo;t forget to allow enough buffer time to allow the file to save to memory</span></p><p class=\"c0\"><span class=\"c1\"></span></p><p class=\"c2\"><span class=\"c1 c4\">Advanced Camera Settings:</span><span class=\"c1\">&nbsp;this allows access to detailed interval cycle settings, the sum of all settings equals the Interval.</span></p><p class=\"c2\"><span class=\"c1\">&nbsp;</span></p><p class=\"c2\"><span class=\"c1 c4\">Focus:</span><span class=\"c1\">&nbsp;amount of time a focus signal is sent to the camera, typically autofocus is not used when shooting timelapse but a short focus signal can clear the camera&rsquo;s auto-review or wake it from a &lsquo;soft-sleep&rsquo; and get it ready for the trigger</span></p><p class=\"c2\"><span class=\"c1 c4\">Trigger:</span><span class=\"c1\">&nbsp;amount of time the trigger signal is sent to the camera, typically 0.1 to 0.2 is enough time to trigger cameras, alternately this setting can be used for HDR sequences (by adjusting the trigger long enough for entire AEB sequences), alternatively it can be used to get very long exposures using the camera&rsquo;s Bulb mode.</span></p><p class=\"c2\"><span class=\"c1 c4\">Delay:</span><span class=\"c1\">&nbsp;is the same setting as Exposure in the main screen, this setting is intended to let the controller know how long it should hold off on motor movements after triggering the camera</span></p><p class=\"c2\"><span class=\"c1 c4\">Buffer:</span><span class=\"c1\">&nbsp;is any left over time after Focus, Trigger and Delay. Another way to put this is Buffer = Interval - Focus - Trigger - Delay.</span></p><p class=\"c2\"><span class=\"c1 c4\">Interval:</span><span class=\"c1\">&nbsp;is the overall cycle time, it cannot be set lower than the sum of all the parts above.</span></p><p class=\"c0\"><span class=\"c1\"></span></p><p class=\"c2\"><span class=\"c1 c4\">Test Interval:</span><span class=\"c1\">&nbsp;used to test the current interval settings with the camera. Motors do not move, only the camera triggle cycle will activate during the test.</span></p><p class=\"c0\"><span class=\"c1\"></span></p><p class=\"c2\"><span class=\"c1 c4\">Shot Duration:</span><span class=\"c1\">&nbsp;the overall shot time, in other words total time the system will run. If changed the Frames below will adjust to match</span></p><p class=\"c0\"><span class=\"c1\"></span></p><p class=\"c2\"><span class=\"c1 c4\">Frames:</span><span class=\"c1\">&nbsp;the total frames that will be shot based on the Interval and Shot Duration</span></p><p class=\"c0\"><span class=\"c1\"></span></p><p class=\"c2\"><span class=\"c1 c4\">Video Length:</span><span class=\"c1\">&nbsp;based on the FPS and frames the video length is the total rendered video time.</span></p><p class=\"c0\"><span class=\"c1\"></span></p><p class=\"c2\"><span class=\"c1 c4\">Load - Save - Restore:</span><span class=\"c1\">&nbsp;allows program setup to be saved to memory for recall later</span></p></body></html>";
    
    
    NSString *screen4 = @"<html><head><meta content=\"text/html; charset=UTF-8\" http-equiv=\"content-type\"><style type=\"text/css\">ol{margin:0;padding:0}.c0{orphans:2;widows:2;direction:ltr;height:11pt}.c6{background-color:#fff;max-width:451.4pt;padding:72pt 72pt 72pt 72pt}.c5{font-size: 12pt;color: #222222}.c2{orphans:2;widows:2;direction:ltr}.c7{font-size: 9.5pt;color: #a61c00}.c1{font-size: 9.5pt;color: #222222}.c3{text-decoration:underline}.c4{font-weight:bold}.title{padding-top:0pt;color:#000000;font-size:26pt;padding-bottom:3pt;font-family:\"Arial\";line-height:1.15;page-break-after:avoid;orphans:2;widows:2;text-align:left}.subtitle{padding-top:0pt;color:#666666;font-size:15pt;padding-bottom:16pt;font-family:\"Arial\";line-height:1.15;page-break-after:avoid;orphans:2;widows:2;text-align:left}li{color:#000000;font-size:11pt;font-family:\"Arial\"}p{margin:0;color:#000000;font-size:11pt;font-family:\"Arial\"}h1{padding-top:20pt;color:#000000;font-size:20pt;padding-bottom:6pt;font-family:\"Arial\";line-height:1.15;page-break-after:avoid;orphans:2;widows:2;text-align:left}h2{padding-top:18pt;color:#000000;font-size:16pt;padding-bottom:6pt;font-family:\"Arial\";line-height:1.15;page-break-after:avoid;orphans:2;widows:2;text-align:left}h3{padding-top:16pt;color:#434343;font-size:14pt;padding-bottom:4pt;font-family:\"Arial\";line-height:1.15;page-break-after:avoid;orphans:2;widows:2;text-align:left}h4{padding-top:14pt;color:#666666;font-size:12pt;padding-bottom:4pt;font-family:\"Arial\";line-height:1.15;page-break-after:avoid;orphans:2;widows:2;text-align:left}h5{padding-top:12pt;color:#666666;font-size:11pt;padding-bottom:4pt;font-family:\"Arial\";line-height:1.15;page-break-after:avoid;orphans:2;widows:2;text-align:left}h6{padding-top:12pt;color:#666666;font-size:11pt;padding-bottom:4pt;font-family:\"Arial\";line-height:1.15;page-break-after:avoid;font-style:italic;orphans:2;widows:2;text-align:left}</style></head><body class=\"c6\"><p class=\"c2\"><span class=\"c3 c5\">Two Point Keyframe Control</span><p class=\"c0\"><span class=\"c1\"></span></p><p class=\"c2\"><span class=\"c1\">With Two Point keyframe control you have the ability to set each axis independently; start time (lead in), amount of time it takes to ramp up-to &lsquo;cruising&rsquo; speed, cruising speed time, ramp down time and any desired lead out (motor still) time. Keyframes can slide to be set or double tapping the keyframe will bring up a dialog to set an exact time or frame</span></p><p class=\"c0\"><span class=\"c1\"></span></p><p class=\"c2\"><span class=\"c1 c4\">Lock:</span><span class=\"c1\">&nbsp;when activated all keyframes will move together in unison</span></p><p class=\"c0\"><span class=\"c1\"></span></p><p class=\"c2\"><span class=\"c1\">Ramping profile choices:</span></p><p class=\"c2\"><span class=\"c1 c4\">Linear:</span><span class=\"c1\">&nbsp;acceleration and deceleration is a constant rate</span></p><p class=\"c2\"><span class=\"c1 c4\">Parabolic:</span><span class=\"c1\">&nbsp;acceleration starts slow and quickly builds speed and deceleration quickly slows down to a gentle stop.</span></p><p class=\"c2\"><span class=\"c1 c4\">Inverse:</span><span class=\"c1\">&nbsp;acceleration starts quickly and slowly builds speed and deceleration gently slows down to a quick stop. &nbsp;</span></p><p class=\"c0\"><span class=\"c1\"></span></p><p class=\"c2\"><span class=\"c5 c3\">Three Point Keyframe Control</span></p><p class=\"c0\"><span class=\"c1\"></span></p><p class=\"c2\"><span class=\"c1\">With Three Point keyframe control all axes move in unison, you can change the timing of the Start, Mid and End by sliding the keyframes on the timeline or double tapping the keyframe to set exact values. If the Start and End keyframes are moved in from the ends this creates still time or lead-in and lead-out time.</span></p></body></html>";
    
